import React, { Component } from "react";

class Landing extends Component {
  // A method to calculate the cost of the currently selected pack
  calculateCost = () => {
    console.log(this.state.bedrooms);
  }

  // A method that will return the value of the calculated cost.
  showValue = () => {
    return (
      <div>
        test
      {this.calculateCost()}

      </div>
    )
  };
  // This will handle setting the state when button choices are changed
  handleChange = (event) => {
    if (event.target.value.includes("bedroom")) {
      this.setState({ bedrooms: event.target.value[0] });
    } else if (event.target.value.includes("bathroom")) {
      this.setState({ bathrooms: event.target.value[0] });
    } else {
      this.setState({ choice: event.target.value });
    }
  };

  // A form containing the select buttons on the homepage.
  form = () => {
    return (
      <form onChange={this.handleChange}>
        <select>
          <option value="1 bedroom">1 bedroom</option>
          <option value="2 bedroom">2 bedroom</option>
          <option value="3 bedroom">3 bedroom</option>
          <option value="4 bedroom">4 bedroom</option>
          <option value="5 bedroom">5 bedroom</option>
        </select>

        <select>
          <option value="1 bathroom">1 bathroom</option>
          <option value="2 bathroom">2 bathroom</option>
          <option value="3 bathroom">3 bathroom</option>
          <option value="4 bathroom">4 bathroom</option>
        </select>

        <select>
          <option value="Standard">Standard </option>
          <option value="Deluxe">Deluxe </option>
          <option value="">Deep clean </option>
          <option value="Deep">Moving in/out </option>
        </select>

        <input type="submit" name="submit" />

        {this.showValue()}
      </form>
    );
  };
  render() {
    console.log(this.state);
    return (
      <>
        <h1>On Landing</h1>
        <div>{this.form()}</div>
      </>
    );
  }
}
export default Landing;
